{
  "metadata": {
    "name": "f1-backend-v2",
    "tags": []
  },
  "desiredState": {
    "ingress": {
      "uris": {
        "/api/f1/drivers": {
          "get": {
            "operationId": "listDrivers",
            "parameters": [
              {
                "description": "How many items to return at one time (max 100)",
                "in": "query",
                "name": "limit",
                "required": false,
                "schema": {
                  "format": "int32",
                  "type": "integer"
                }
              }
            ],
            "responses": {
              "200": {
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/ResultDriverList"
                    }
                  }
                },
                "description": "A paged array of drivers",
                "headers": {
                  "x-next": {
                    "description": "A link to the next page of responses",
                    "schema": {
                      "type": "string"
                    }
                  }
                }
              },
              "default": {
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                },
                "description": "unexpected error"
              }
            },
            "summary": "List all drivers",
            "tags": [
              "drivers"
            ]
          },
          "post": {
            "operationId": "createDrivers",
            "responses": {
              "201": {
                "description": "Null response"
              },
              "default": {
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                },
                "description": "unexpected error"
              }
            },
            "summary": "Create a driver",
            "tags": [
              "drivers"
            ]
          }
        },
        "/api/f1/seasons": {
          "get": {
            "operationId": "listSeasons",
            "responses": {
              "200": {
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/ResultSeasonList"
                    }
                  }
                },
                "description": "Expected response to a valid request"
              },
              "default": {
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                },
                "description": "unexpected error"
              }
            },
            "summary": "A paged array of seasons",
            "tags": [
              "seasons"
            ]
          }
        }
      },   
      "gatewayRefs": [
        {
          "ref": "/services/environments/prod/gateways/apigw"
        }
      ]
    },
    "backend": {
      "ntlmAuthentication": "DISABLED",
      "preserveHostHeader": "DISABLED",
      "workloadGroups": {
        "ergast": {
          "loadBalancingMethod": {
            "type": "ROUND_ROBIN"
          },
          "uris": {
            "http://{{ERGAST01}}:8002": {
              "isBackup": false,
              "isDown": false,
              "isDrain": false
            }
          }
        }
      }
    },
    "logging": {
      "errorLog": "DISABLED",
      "accessLog": {
        "state": "DISABLED"
      }
    },
    "errorSetRef": {
      "ref": "/services/errorsets/default-json"
    },
    "publishedApiRefs": [
      {
        "ref": "/services/environments/prod/apps/sports-results/published-apis/f1-v2-prod"
      }
    ]
  }
}
